import React, { useState, useEffect } from 'react';
import './App.css';
import { Modal, Button } from 'react-bootstrap';

interface Website {
  name: string;
  url: string;
}

function App() {
  const [websites, setWebsites] = useState<Website[]>([]);
  const [newSiteName, setNewSiteName] = useState('');
  const [newSiteUrl, setNewSiteUrl] = useState('');
  const [theme, setTheme] = useState('light');
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  useEffect(() => {
    const savedTheme = localStorage.getItem('teslahub_theme');
    const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (savedTheme) {
      setTheme(savedTheme);
    } else if (prefersDark) {
      setTheme('dark');
    }
  }, []);

  useEffect(() => {
    document.body.className = `${theme}-mode`;
    localStorage.setItem('teslahub_theme', theme);
  }, [theme]);

  useEffect(() => {
    const storedWebsites = localStorage.getItem('teslahub_websites');
    if (storedWebsites) {
      setWebsites(JSON.parse(storedWebsites));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('teslahub_websites', JSON.stringify(websites));
  }, [websites]);

  const handleAddWebsite = (e: React.FormEvent) => {
    e.preventDefault();
    if (newSiteName && newSiteUrl) {
      setWebsites([...websites, { name: newSiteName, url: newSiteUrl }]);
      setNewSiteName('');
      setNewSiteUrl('');
      handleClose();
    }
  };

  const handleDeleteWebsite = (index: number) => {
    const newWebsites = [...websites];
    newWebsites.splice(index, 1);
    setWebsites(newWebsites);
  };

  const handleOpenInTesla = (url: string) => {
    window.open(`https://www.youtube.com/redirect?q=${encodeURIComponent(url)}`, '_blank');
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={theme === 'light' ? 'light-mode' : 'dark-mode'}>
      <div className="container-fluid text-center py-4 bg-dark text-white">
        <div className="d-flex justify-content-end">
            <button className="btn btn-outline-light" onClick={toggleTheme}>
              {theme === 'light' ? 'Dark' : 'Light'} Mode
            </button>
        </div>
        <h1 className="display-4">TeslaHub</h1>
        <p className="lead">Your Personal Tesla Companion</p>
      </div>
      <div className="container mt-4">
        <div className="d-grid gap-2">
            <Button variant="primary" size="lg" onClick={handleShow}>
                Add a new website
            </Button>
        </div>

        <Modal show={showModal} onHide={handleClose} centered>
          <Modal.Header closeButton>
            <Modal.Title>Add a new website</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form onSubmit={handleAddWebsite}>
              <div className="mb-3">
                <label htmlFor="siteName" className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="siteName"
                  value={newSiteName}
                  onChange={(e) => setNewSiteName(e.target.value)}
                  placeholder="e.g., KKTV"
                />
              </div>
              <div className="mb-3">
                <label htmlFor="siteUrl" className="form-label">URL</label>
                <input
                  type="url"
                  className="form-control"
                  id="siteUrl"
                  value={newSiteUrl}
                  onChange={(e) => setNewSiteUrl(e.target.value)}
                  placeholder="e.g., https://www.kktv.me/"
                />
              </div>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
              <Button variant="primary" type="submit">
                Add
              </Button>
            </form>
          </Modal.Body>
        </Modal>

        <div className="card mt-4">
          <div className="card-header">My Websites</div>
          <ul className="list-group list-group-flush">
            {websites.map((site, index) => (
              <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                {site.name}
                <div>
                  <button className="btn btn-success me-2" onClick={() => handleOpenInTesla(site.url)}>
                    Open in Tesla
                  </button>
                  <button className="btn btn-danger" onClick={() => handleDeleteWebsite(index)}>
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
